function _goto(target_block, speed, offset = 0) {
	const moveTo = new MoveTo({
		tolerance: offset + document.querySelector('.header__wrapper').offsetHeight + 50,
		duration: speed,
		easing: 'easeOutQuart'
	});
	moveTo.move(target_block);
}

// *accurate page left/top coordinates
function offset(el) {
	var rect = el.getBoundingClientRect(),
		scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
		scrollTop = window.pageYOffset || document.documentElement.scrollTop;
	return { top: rect.top + scrollTop, left: rect.left + scrollLeft }
}

// *Animation on scroll
const animItems = document.querySelectorAll('._anim-items');
if (animItems.length > 0) {
	window.addEventListener('scroll', animOnScroll);
	function animOnScroll() {
		for (let index = 0; index < animItems.length; index++) {
			const animItem = animItems[index];
			const animItemHeight = animItem.offsetHeight;
			const animItemOffset = animItem.getBoundingClientRect().top + pageYOffset;
			const animStart = 4;
			let animItemPoint = window.innerHeight - animItemHeight / animStart;
			if (animItemHeight > window.innerHeight) {
				animItemPoint = window.innerHeight - window.innerHeight / animStart;
			}

			if ((pageYOffset > animItemOffset - animItemPoint) && pageYOffset < (animItemOffset + animItemHeight)) {
				animItem.classList.add('_anim')
			} else {
				if (!animItem.classList.contains('_anim-no-hide')) {
					animItem.classList.remove('_anim');
				}
			}
		}
	}
	setTimeout(() => {
		animOnScroll();
	}, 300);
}
// * Минни версия Динамического адаптива
const item = document.querySelector('.header__button-parent');
if (item) {
	const parent_original = document.querySelector('.header__body');
	const parent = document.querySelector('.menu__body');
	function dynamicAdaptive(e) {
		if (e.matches) {
			if (!item.classList.contains('done')) {
				parent.insertBefore(item, parent.children[1])
				item.classList.add('done')
			}
		} else {
			if (item.classList.contains('done')) {
				parent_original.insertBefore(item, parent_original.children[2])
				item.classList.remove('done')
			}
		}
	}
	const mediaWidth = window.matchMedia('(max-width: 479.98px)');
	mediaWidth.addListener(dynamicAdaptive)
	dynamicAdaptive(mediaWidth);
}